mapper.update:
UPDATE pr_normal_batch SET status = #{status}, comments = #{comments},
result_data = #{result},
modified_by = #{modifiedBy}, modified_time = sysdate() WHERE code = #{code}

mapper.select:
SELECT code FROM pr_normal_batch WHERE management_id = #{managementId}
UNION
SELECT ab.adjust_batch_code as code FROM pr_adjust_batch ab JOIN pr_normal_batch nb ON nb.code = ab.origin_batch_code WHERE nb.management_id = #{managementId}
UNION
SELECT btb.back_tracking_batch_code as code FROM pr_back_tracking_batch btb JOIN pr_normal_batch nb ON nb.code = btb.origin_batch_code WHERE nb.management_id = #{managementId}
