mapper.select:
SELECT *
FROM prs_payroll_template
WHERE is_active = 1

  and template_code = #{templateCode}


  and template_name like concat('%', #{templateName}, '%')


  and management_id = #{managementId}


  and management_name like concat('%', #{managementName}, '%')

ORDER BY id DESC

  LIMIT #{limit}


  OFFSET #{offset}
