SELECT t1.salary_grant_sub_task_id taskId , t1.salary_grant_sub_task_code taskCode , t1.task_type taskType , t1.task_status taskStatus , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_account_code grantAccountCode, t1.grant_account_name grantAccountName, date_format(t1.created_time, '%Y-%m-%d') createdTimeStr, t1.grant_date grantDate , t1.grant_time grantTime , t1.remark FROM sg_salary_grant_sub_task t1 WHERE t1.task_status = '0' AND t1.is_active = 1 AND t1.task_type = 2 AND t1.grant_mode = 2
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id IN (${managementIds})
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
ORDER BY t1.salary_grant_sub_task_id

SELECT t1.salary_grant_sub_task_id taskId , t1.salary_grant_sub_task_code taskCode , t1.task_type taskType , t1.task_status taskStatus , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_account_code grantAccountCode, t1.grant_account_name grantAccountName, t1.remark FROM sg_salary_grant_sub_task t1 WHERE t1.task_status = '1' AND t1.is_active = 1 AND t1.task_type = 2 AND t1.grant_mode = 2
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id IN (${managementIds})
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
ORDER BY t1.salary_grant_sub_task_id

SELECT t1.salary_grant_sub_task_id taskId , t1.salary_grant_sub_task_code taskCode , t1.task_type taskType , t1.task_status taskStatus , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_account_code grantAccountCode, t1.grant_account_name grantAccountName, t1.remark FROM sg_salary_grant_sub_task t1 WHERE t1.task_status = '1' AND t1.is_active = 1 AND t1.task_type = 2 AND t1.grant_mode = 2
AND t1.operator_user_id = #{currentUserId}
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id IN (${managementIds})
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
ORDER BY t1.salary_grant_sub_task_id

SELECT t1.salary_grant_sub_task_id taskId , t1.salary_grant_sub_task_code taskCode , t1.task_type taskType , t1.task_status taskStatus , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_account_code grantAccountCode, t1.grant_account_name grantAccountName, t1.approved_opinion approvedOpinion FROM sg_salary_grant_sub_task t1 WHERE t1.task_status IN ('2' , '10', '11') AND t1.is_active = 1 AND t1.task_type = 2 AND t1.grant_mode = 2
AND (t1.approve_user_id = #{currentUserId} OR t1.operator_user_id = #{currentUserId})
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id IN (${managementIds})
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
ORDER BY t1.salary_grant_sub_task_id

SELECT t1.salary_grant_sub_task_id taskId , t1.salary_grant_sub_task_code taskCode , t1.task_type taskType , t1.task_status taskStatus , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_date grantDate , t1.grant_type grantType , '' AS grantTypeName , t1.grant_account_code grantAccountCode, t1.grant_account_name grantAccountName FROM sg_salary_grant_sub_task t1 WHERE t1.task_status = '12' AND t1.is_active = 1 AND t1.task_type IN (3 , 4)
AND t1.operator_user_id = #{currentUserId}
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id IN (${managementIds})
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
AND t1.grant_cycle = #{grantCycle}
ORDER BY t1.salary_grant_sub_task_id
