AND t1.operator_user_id = #{currentUserId}
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id = #{managementId}
AND t1.management_name LIKE concat('%', #{managementName}, '%')
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
ORDER BY t1.salary_grant_sub_task_code

SELECT t1.salary_grant_sub_task_id taskId , t1.salary_grant_sub_task_code taskCode , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_account_code grantAccountCode, '' AS grantAccountName, t1.approved_opinion approvedOpinion FROM sg_salary_grant_sub_task t1 WHERE t1.task_status IN ('2' , '10', '11') AND t1.is_active = 1 AND t1.task_type = 2 AND t1.grant_mode = 2
AND (t1.approve_user_id = #{currentUserId} OR t1.operator_user_id = #{currentUserId})
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id = #{managementId}
AND t1.management_name LIKE concat('%', #{managementName}, '%')
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
ORDER BY t1.salary_grant_sub_task_code

SELECT t1.task_id taskId , t1.task_code taskCode , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_account_code grantAccountCode, '' AS grantAccountName, t1.approved_opinion approvedOpinion FROM sg_salary_grant_task_history t1 WHERE t1.task_status IN ('3' , '8') AND t1.is_active = 1 AND t1.task_type = 2 AND t1.grant_mode = 2
AND (t1.approve_user_id = #{currentUserId} OR t1.operator_user_id = #{currentUserId})
AND t1.task_code = #{taskCode}
AND t1.management_id = #{managementId}
AND t1.management_name LIKE concat('%', #{managementName}, '%')
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
ORDER BY t1.task_code

SELECT t1.salary_grant_sub_task_id taskId , t1.salary_grant_sub_task_code taskCode , t1.management_name managementName , t1.management_id managementId , t1.batch_code batchCode , t1.grant_cycle grantCycle , t1.payment_total_sum paymentTotalSum , t1.total_person_count totalPersonCount, t1.chinese_count chineseCount , t1.foreigner_count foreignerCount , t1.grant_date grantDate , t1.grant_type grantType , case t1.grant_type when 1 then '正常发放' when 2 then '调整发放' when 3 then '回溯发放' when 4 then '暂缓再发放' when 5 then '退票发放' when 6 then '现金' else '' end AS grantTypeName , t1.grant_account_code grantAccountCode, '' AS grantAccountName FROM sg_salary_grant_sub_task t1 WHERE t1.task_status = '12' AND t1.is_active = 1 AND t1.task_type IN (3 , 4)
AND t1.operator_user_id = #{currentUserId}
AND t1.salary_grant_sub_task_code = #{taskCode}
AND t1.management_id = #{managementId}
AND t1.management_name LIKE concat('%', #{managementName}, '%')
AND t1.batch_code = #{batchCode}
AND t1.grant_account_code = #{grantAccountCode}
AND t1.grant_cycle = #{grantCycle}
ORDER BY t1.salary_grant_sub_task_code
