mapper.update:
update tax_fc_task_sub_supplier
set is_active = false,
    modified_by = #{modifiedBy},
    modified_time = #{modifiedTime}
where id = #{combinedId}

update tax_fc_task_sub_supplier
set is_active = true,
    task_sub_supplier_id = null,
    modified_by = #{modifiedBy},
    modified_time = #{modifiedTime}
where task_sub_supplier_id=#{combinedId}
